import React from "react";
import styled, { css } from 'styled-components';
import { NavLink as Link } from "react-router-dom";

import * as colors from "../../colors";
import Arrow from "../../images/arrow-icon.png";
import SearchWhite from "../../images/search-icon-white.png";
import Burger from "../../images/burger.png"

export default class SideNavBar extends React.Component {
  constructor (props) {
    super(props);

    this.state = {
      isOpen: false
    };
  }

  slideNav = () => {
    this.setState({isOpen: false})
  }
  /* Write the necessary functions to show and hide the side bar on small devices */

  render () {
    // const { isOpen } = true;
    // this state

    return (
      <>
        <Hamburger src={Burger} onClick={this.slideNav}></Hamburger>
        <SideNavBarCont className={this.state.isOpen ? 'visible' : ''}>
          {/* Implement a hamburger icon slide in effect for small devices */}
          <SideNavMainLink className="menu_nav_link main_nav_link" to="/" exact>
            Wesley
            <NavIcon src={Arrow}></NavIcon>
          </SideNavMainLink>
          <SideNavMainLink className="menu_nav_link" to="/discover">
            Discover
            <NavIcon src={SearchWhite}></NavIcon>
          </SideNavMainLink>
          <SideNavHeader onClick={this.slideNav}><HeaderText>Watched</HeaderText></SideNavHeader>
          <NavLink className="menu_nav_link" to="/watched/movies">Movies</NavLink>
          <NavLink className="menu_nav_link" to="/watched/tv-shows">Tv Shows</NavLink>
          <SideNavHeader><HeaderText>Saved</HeaderText></SideNavHeader>
          <NavLink className="menu_nav_link" to="/saved/movies">Movies</NavLink>
          <NavLink className="menu_nav_link" to="/saved/tv-shows">Tv Shows</NavLink>
        </SideNavBarCont>
      </>
    );
  }
}

const SideNavBarCont = styled.div`
  position: fixed;
  z-index: 9;
  width: 280px;
  height: 100%;
  background-color: ${colors.sideNavBar};
  padding-left: 30px;

  @media (max-width: 768px) {
    display: none;
  }
`

const SideNavMainLink = styled(Link)`
  position: relative;
  display: block;
  padding-top: 25px ;
  font-size: 1.6em;
  font-weight: 700;
  color: white;
  &:hover{
    background-color: ${colors.sideNavBarHover};
  }
`

const NavIcon = styled.img`
  position: absolute;
  right: 35px;
  top: 50%;
`

const SideNavHeader = styled.div`
  border-bottom: 1px solid grey;
  padding-top: 25px;
  padding-bottom: 10px;
  margin-top: 15px;
`

const HeaderText = styled.div`
  font-size: 1.2em;
  font-weight: 300;
  color: white;
`

const NavLink = styled(Link)`
  display: block;
  color: grey;
  padding-top: 10px;

`
const Hamburger = styled.img`
  display: none;

  @media (max-width: 768px) {
    width: 60px;
    display: flex;
    z-index: 20;
    margin: 30px;

  }

`
