import SideNavBar from "./components/sidenavbar/index";

import Discover from "./pages/discover";

import './css/app.css'; 

export default class App extends React.Component {
  render () {
    return (
      <Router>
        <PageContainer>
          <NavCon>
            <SideNavBar {...this.props} />
            {/* <SearchCon>
                <SearchLogo src={SearchIcon}></SearchLogo>
