import React from "react";
import styled from 'styled-components';
import * as colors from "../../colors";


export default class MovieItem extends React.Component {

  render () {
    const { image, genre, title, overview, rate, date } = this.props;
    console.log(title)

    return (
      // Complete the MovieItem component
      <MovieItemWrapper>
        <LeftCont>
          <ItemImage src={`https://image.tmdb.org/t/p/w500/${image}`}></ItemImage>
        </LeftCont>
        <RightCont>
          <ItemContent>
            <ContentHeader>
              <ContentTitle>
                <ContentName>{title}</ContentName>
                <ContentGenre>{genre}</ContentGenre>
              </ContentTitle>
              <ContentRating>{rate}</ContentRating> 
            </ContentHeader>
            <ContentFooter>
              {overview}
            </ContentFooter>
            <ContentDate>{date}</ContentDate>
          </ItemContent>
        </RightCont>
      </MovieItemWrapper>
    )
  }
}

const MovieItemWrapper = styled.div`
  position: relative;
  background-color: white;
  width: 100%;
  height: 200px;
  border-radius: 3px;
  display: flex;
  flex-direction: row;
  margin-top: 15px;
  padding: 5px;
  overflow: hidden;

`

const LeftCont = styled.div`
  width: 30%;
  height: 100%;

`

const RightCont = styled.div`
  width: 70%;
  height: 100%;
  display: inline-block;
`

const ItemImage = styled.img`
width: 100%;
  height: 100%;
`
const ItemContent = styled.div`
  display: flex;
  flex-direction: column;
  padding: 15px;

`

const ContentHeader = styled.div`
  display: flex;
  justify-content: space-between;
 
`
const ContentTitle = styled.div`
  top: 0;
`
const ContentFooter = styled.div`
  height: 80px;
  overflow: hidden;
  margin-top: 10px;
  margin-bottom: 10px;

`
const ContentDate = styled.div`
  color: ${colors.primaryColor};
  font-weight: 100;
  font-size: x-small;
`

const ContentName = styled.div`
  font-weight: 700;
  font-size: x-large;
`
const ContentGenre = styled.div`
  font-weight: 300;
  color:${colors.primaryColor} ;
`
const ContentRating = styled.div`
  color: white;
  background-color:${colors.primaryColor};
  padding: 3px;
  border-radius: 5px;
  height: 20px;
`
