import React from "react";
import styled from 'styled-components';

import * as colors from "../../colors";
import SearchIcon from "../../images/search-icon-yellow.png";
import CalendarIcon from "../../images/year-icon.png";

export default class SearchBar extends React.Component {
    
    // handleSearch = (e) => {
    //     this.props.searchMovies(e.target.value)
    // }
    
    render () {

        return (
            <SearchWrapper>
                <SearchCon>
                    <SearchLogo src={SearchIcon}></SearchLogo>
                    
                    <SearchInput placeholder="Name of Movies" onChange={this.props.handleKeyword}></SearchInput>
                </SearchCon>
                <SearchCon>
                    <SearchCalender src={CalendarIcon}></SearchCalender>
                    <SearchInput placeholder="Year of Release" onChange={this.handleSearch}></SearchInput>
                </SearchCon>
            </SearchWrapper>
        )
        
    }           
}
const SearchWrapper = styled.div`
    display: flex;
    flex-direction: column;
      background-color: white;
      border-radius: 3px;
    
`
const SearchCon = styled.div`
    display: flex;
    flex-direction: row;
    border-bottom: 1px solid ;
    border-color: ${colors.primaryColor};
`
const SearchLogo = styled.img`
    width: 30px;
    height: 30px;
    `

const SearchInput = styled.input`
    padding: 20px;
    border: none;
    outline: none;    
`

const SearchCalender = styled.img`
    width: 30px;
    height: 30px;
`
