import React from "react";
import styled from 'styled-components';

import MovieItem from '../movieitem';

export default class MovieList extends React.Component {
  state = {
    genre: []
  }

  // componentDidMount() {
  //   fetch("https://api.themoviedb.org/3/movie/524434?api_key=885b085ddf759af703d71ca6be89d58b&language=en-US")
  //   .then((response) => response.json())
  //   .then(response => {
  //       this.setState({
  //         genre: response.genres
  //       })
  //       console.log(response.genres)
  //   });
  // }

  

  render () {
    const { movies }  = this.props;

    return (
      <MoviesWrapper>
        {movies.map(item => {
          fetch(`https://api.themoviedb.org/3/movie/${item.id}?api_key=885b085ddf759af703d71ca6be89d58b&language=en-US`)
          .then((response) => response.json())
          .then(response => {
              this.setState({
                genre: response.genres
              })
              console.log(response)
          });
          return (
            <MovieItem 
              key={item.id}
              image={item.backdrop_path}
              title={item.title}
              genre={item.genre_ids}
              rate={item.vote_average}
              overview={item.overview}
              date={item.release_date}
            />
          )
        })}
      </MoviesWrapper>
    )
  }
}

const MoviesWrapper = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
`
