import React from "react";
import styled from 'styled-components';


import Checkbox from "../checkbox";
import Plus from "../../images/plus.png"
import SearchWhite from "../../images/search-icon-white.png";


export default class ExpandableFilter extends React.Component {
  constructor (props) {
    super(props);

    this.state = {
      filtersShown: true
    };
  }

  handleIcon = () => {
    this.setState({
      filtersShown: !this.state.filtersShown
    })
  }

  render() {
 
    return (
      <ExpandCon>
        <ExpandHeader>
          <IconHeader src={this.state.filtersShown ? Arrow : Plus}></IconHeader>
          <TitleHeader>Select Genre(s)</TitleHeader>
        </ExpandHeader>

      </ExpandCon>
    )
  }

  // You need to create your own checkbox component with a custom checkmark
}


const ExpandCon = styled.div`
  margin-top: 10px;
  padding: 10px;
`
const ExpandHeader = styled.div`
  display: flex;
  justify-content: space-between;
`
const IconHeader = styled.img`
 background-color: black;
  width: 20px;
  color: black;
`
const TitleHeader = styled.span`

`
